local self = {}CGUI.ValidatorChain = CUtil.MakeConstructor (self)function self:ctor (host)	CUtil.EventProvider (self)	self.Validators = {}		if host then		host.AddValidator = function (host, validator)			self:AddValidator (validator)		end	endendfunction self:AddValidator (validator)	self.Validators [#self.Validators + 1] = validatorendfunction self:Validate (...)	local valid, error = true, nil	for _, validator in ipairs (self.Validators) do		valid, error = validator (...)		if not valid then			self:DispatchEvent ("Validated", valid, error)			return valid, error		end	end	self:DispatchEvent ("Validated", valid, nil)	return trueend