local self = CInventory.Actions:CreateAction ("ShipmentDrop")function self:CanRun (ply, inventory, item)	return inventory:GetOwnerEntity () and true or falseendfunction self:Run (ply, inventory, item)	local tr = inventory:GetOwnerEntity ():GetEyeTrace ()	if not tr.Hit then		return	end	local dist = (tr.HitPos - tr.StartPos):Length ()	local pos = tr.HitPos	if dist > 80 then		pos = tr.StartPos + 80 * (tr.HitPos - tr.StartPos):GetNormalized ()	end	local ent = ents.Create ("spawned_shipment")	local class = item:GetEntityClass ():lower ()	for k, v in pairs (CustomShipments) do		if v.entity:lower () == class then			ent.dt.contents = k			break		end	end	ent.dt.count = item.Amount	if item:GetModel ():sub (1, 6):lower () == "models" then		ent:SetModel (item:GetModel ())	end	ent:SetPos (pos)	ent:Spawn ()	ent:Activate ()		ent:TryCall ("OnInventorySpawned", "drop")		if ent.CPPISetOwner then		ent:CPPISetOwner (inventory:GetOwnerEntity ())	end	ent.dt.owning_ent = inventory:GetOwnerEntity ()		if item:GetCount () > 1 then		item:RemoveCount (1)	else		inventory:RemoveItem (item)	endend