local ItemClassGenerator = {}ItemClassGenerator.__index = ItemClassGeneratorCInventory.ItemClassGenerator = CUtil.MakeConstructor (ItemClassGenerator)function ItemClassGenerator:ctor (name)	self.Name = name	self.Weight = 0endfunction ItemClassGenerator:CreateItemClass (name, parameters)	return CInventory.GenericItemClass (name, parameters)endfunction ItemClassGenerator:GetName ()	return self.Nameendfunction ItemClassGenerator:GetWeight ()	return self.Weightendfunction ItemClassGenerator:SetName (name)	self.Name = nameend